(()=>{const e=document.querySelector("#container"),t=document.querySelector("#city"),c=document.querySelector("#country"),n=document.querySelector("#degreesC"),r=document.querySelector("#degreesF"),o=document.querySelector("#feelsLikeC"),u=document.querySelector("#feelsLikeF"),i=document.querySelector("#humidity"),l=document.querySelector("#location");document.querySelector("#search").addEventListener("click",(async s=>{s.preventDefault();const d=l.value.trim();if(d){const l=await async function(e){const t=await fetch(`https://api.weatherapi.com/v1/current.json?key=33417db233ee48d59c313733240603&q=${e}`,{mode:"cors"}),c=await t.json();return{degrees_c:c.current.temp_c,degrees_f:c.current.temp_f,feelsLike_c:c.current.feelslike_c,feelsLike_f:c.current.feelslike_f,humidity:c.current.humidity,country:c.location.country,city:c.location.name}}(d);n.textContent=`Degrees: ${l.degrees_c} 째C`,r.textContent=`Degrees: ${l.degrees_f} 째F`,o.textContent=`Feels like: ${l.feelsLike_c} 째C`,u.textContent=`Feels like: ${l.feelsLike_f} 째F`,i.textContent=`Humidity: ${l.humidity}`,t.textContent="City: "+l.city,c.textContent="Country: "+l.country,e.classList.add("result")}else alert("Please enter a location")}))})();